import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [address, setAddress] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);
  const [showAddressForm, setShowAddressForm] = useState(false);

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/cart', {
            headers: {
                Authorization:`Bearer ${localStorage.getItem('token')}`
              }
        });
        setCartItems(response.data.cartItems);
        calculateTotalPrice(response.data.cartItems);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };

    fetchCartItems();
  }, []);

  const calculateTotalPrice = (items) => {
    let total = 0;
    items.forEach(item => {
      total += item.price * item.quantity;
    });
    setTotalPrice(total);
  };

  const handleRemoveFromCart = async (productId) => {
    try {
      const response = await axios.delete(`http://localhost:5000/api/cart/remove/${productId}`, {
        headers: {
            Authorization:`Bearer ${localStorage.getItem('token')}`
          }
      });
      setCartItems(response.data.cartItems);
      calculateTotalPrice(response.data.cartItems);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const handleDelivery = async () => {
    setShowAddressForm(true);
  };

  const handleAddAddress = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/address', { address, phoneNumber }, {
        headers: {
            Authorization:`Bearer ${localStorage.getItem('token')}`
          }
      });
      console.log('Address added successfully:', response.data);
      setAddress('');
      setPhoneNumber('');
      setShowAddressForm(false);
    } catch (error) {
      console.error('Error adding address:', error);
    }
  };

  return (
    <div>
      <h2>Cart</h2>
      <ul>
        {cartItems.map(item => (
          <li key={item.productId}>
            {item.productName} - Quantity: {item.quantity} - Price: {item.price * item.quantity}
            <button onClick={() => handleRemoveFromCart(item.productId)}>Remove</button>
          </li>
        ))}
      </ul>

      <h2>Total Price: {totalPrice}</h2>

      <h2>Delivery</h2>
      <button onClick={handleDelivery}>Delivery</button>

      {showAddressForm && (
        <div>
          <h2>Add Address</h2>
          <div>
            <label>Address:</label>
            <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
          </div>
          <div>
            <label>Phone Number:</label>
            <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
          </div>
          <button onClick={handleAddAddress}>Add Address</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
