import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import './App.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Buyerr from "./buyerrr";
import Sellerr from "./sellerr";
import LandingPage from "./landingPage";
import { AuthProviderr } from "./providersell/AuthProvidersell";
import Logins from "./components/navbar_seller/Login";
import Login from "./components/navbar/Login";
import Navbar from "./components/navbar_seller/navbar";
function App(){
    const [isLoginOpen, setIsLoginOpen] = useState(false);
    const [cartCount,setCartCount]=useState(0);
    const fetchCartCount =async () => {
        try {
          const token = localStorage.getItem('token');
          if (!token) {
            throw new Error('No token found');
          }
          const response = await axios.get('http://localhost:5000/api/cart-sell/count', {
            headers: { Authorization:` Bearer ${token}` }
          });
          if (response.data.success) {
            
           setCartCount(response.data.cartCount);
           console.log("countttt",response.data.cartCount);
          }
        } catch (error) {
          console.error('Error fetching cart count:', error);
        }
      }
      console.log("count1111tt",cartCount);

      useEffect(()=>{
        fetchCartCount();
      }, []);
    
   
 
    return (
        <AuthProviderr>
               
        <Router>
        <Navbar setIsLoginOpen={setIsLoginOpen}  cartCount={cartCount} fetchCartCount={fetchCartCount}/>
        <Routes>
            <Route exact path="/" element={<LandingPage />} />
            <Route path="/buyer" element={<Buyerr />} />
            <Route path="/seller" element={<Sellerr />} />
            <Route exact path="/logins" element={isLoginOpen &&<Logins setIsLoginOpen={setIsLoginOpen} />} />
            <Route exact path="/login" element={isLoginOpen &&<Login setIsLoginOpen={setIsLoginOpen} />} />
            </Routes>
        </Router>
        </AuthProviderr>
    );
}

export default App;
