import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './cartCard';
import './cartpage.css'
const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [address, setAddress] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);
  const [showAddressForm, setShowAddressForm] = useState(false);

 
    const fetchCartItems = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/cart', {
            headers: {
                Authorization:`Bearer ${localStorage.getItem('token')}`
              }
        });
        const cartItemsWithDetails = await Promise.all(response.data.cartItems.map(async (item) => {
          // Fetch product details for each item in the cart
          const productResponse = await axios.get(`http://localhost:5000/api/products/${item.product_id}`);
          const product = productResponse.data;
          return {
            ...item,
            productName: product.name,
            productPrice: product.price
          };
        }));
        setCartItems(cartItemsWithDetails);
        calculateTotalPrice(cartItemsWithDetails);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };



    useEffect(()=>{

    fetchCartItems();
  }, []);

  const calculateTotalPrice = (items) => {
    let total = 0;
    items.forEach(item => {
      total += item.productPrice * item.quantity;
    });
    setTotalPrice(total);
  };

  const handleRemoveFromCart = async (productId) => {
    try {
      const response = await axios.delete(`http://localhost:5000/api/cart/remove/${productId}`, {
        headers: {
            Authorization:`Bearer ${localStorage.getItem('token')}`
          }
      });
    await fetchCartItems();
      calculateTotalPrice(response.data.cartItems);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const handleDelivery = async () => {
    setShowAddressForm(true);
  };

  const handleAddAddress = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/address', { address, phoneNumber }, {
        headers: {
            Authorization:`Bearer ${localStorage.getItem('token')}`
          }
      });
      console.log('Address added successfully:', response.data);
      setAddress('');
      setPhoneNumber('');
      setShowAddressForm(false);
    } catch (error) {
      console.error('Error adding address:', error);
    }
  };

  return (
    <div>
      <h2>Cart</h2>

      <div className="product-cards">
        {cartItems.map(item => (
          <ProductCard key={item.product_id} item={item} handleRemoveFromCart={handleRemoveFromCart} />
        ))}
      </div>
      <h2>Total Price: {totalPrice}</h2>

      <h2>Delivery</h2>
      <button onClick={handleDelivery}>Delivery</button>

      {showAddressForm && (
        <div>
          <h2>Add Address</h2>
          <div>
            <label>Address:</label>
            <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
          </div>
          <div>
            <label>Phone Number:</label>
            <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
          </div>
          <button onClick={handleAddAddress}>Add Address</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
