import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from "./components/navbar/navbar";
import Search from './components/searchbar/searchBar';
import './App.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import BannerSlider from "./components/Banners/BannerSlider";
import Homecategories from "./components/category/Homecatgory";
import Footer1 from "./components/Footer/Footer1";
import ProductList from "./components/products/Productitem"
import Routing from "./routes/Routes";
import { AuthProvider } from "./provider/AuthProvider";
import axios from 'axios';
import Login from "./components/navbar/Login";






function App(){
  const [selectedCategory, setSelectedCategory] = useState('Vegetables');
    const [cartCount,setCartCount]=useState(0);
    const [searchQuery, setSearchQuery] = useState('');
    const fetchCartCount =async () => {
        try {
          const token = localStorage.getItem('token');
          if (!token) {
            throw new Error('No token found');
          }
          const response = await axios.get('http://localhost:5000/api/cart/count', {
            headers: { Authorization:` Bearer ${token}` }
          });
          if (response.data.success) {
            
           setCartCount(response.data.cartCount);
           
          }
        } catch (error) {
          console.error('Error fetching cart count:', error);
        }
      }

      const handleCategorySelect = (category) => {
        setSelectedCategory(category);
      };
      const handleSearch = (query) => {
        setSearchQuery(query);
      };
      
    
    return (
        <div className="app-container">
             <div className="alogin-container">
             <AuthProvider>
             
               <Routing CartCount={cartCount} fetchCartCount={fetchCartCount}/>
               
              </AuthProvider>
             </div>
              
           <Search  onSearch={handleSearch} />
             
           {!searchQuery &&   <div className="slider-container">
             <BannerSlider />
             </div>}
 
           {!searchQuery &&   <Homecategories onCategorySelected={handleCategorySelect}/>}
             <AuthProvider>
             <ProductList fetchCartCount={fetchCartCount} category={selectedCategory}  searchQuery={searchQuery}/>
             </AuthProvider>
             <Footer1 />
           
        </div>
       
    );
}

export default App;
