import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from "./components/navbar/navbar";
import Search from './components/searchbar/searchBar';
import './App.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import BannerSlider from "./components/Banners/BannerSlider";
import Homecategories from "./components/category/Homecatgory";
import Footer1 from "./components/Footer/Footer1";
import ProductList from "./components/products/Productitem"
import Routing from "./routes/Routes";
import { AuthProvider } from "./provider/AuthProvider";
import axios from 'axios';






function App(){
  const [selectedCategory, setSelectedCategory] = useState('Vegetables');
    const [cartCount,setCartCount]=useState(0);
    const fetchCartCount =async () => {
        try {
          const token = localStorage.getItem('token');
          if (!token) {
            throw new Error('No token found');
          }
          const response = await axios.get('http://localhost:5000/api/cart/count', {
            headers: { Authorization:` Bearer ${token}` }
          });
          if (response.data.success) {
            
           setCartCount(response.data.cartCount);
           
          }
        } catch (error) {
          console.error('Error fetching cart count:', error);
        }
      }

      const handleCategorySelect = (category) => {
        setSelectedCategory(category);
      };
      
    
    return (
        <div className="app-container">
             <div className="alogin-container">
             <AuthProvider>
             
               <Routing CartCount={cartCount} fetchCartCount={fetchCartCount}/>
               
              </AuthProvider>
             </div>
              
             <Routes>
        <Route path="/" element={<HomePage handleCategorySelect={handleCategorySelect}  fetchCartCount={fetchCartCount} />} />
      </Routes>
        </div>
       
    );
}


const HomePage = ({ handleCategorySelect, fetchCartCount}) => {
  return (
    <>
    <Search />
             <div className="slider-container">
             <BannerSlider />
             </div>
           
             <Homecategories onCategorySelected={handleCategorySelect}/>
             <AuthProvider>
             <ProductList fetchCartCount={fetchCartCount} category={selectedCategory}/>
             </AuthProvider>
               
             <Footer1 />
             
    </>
  );
};

export default App;
