// Buyer.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from './searchbar/SearchBar';
import Navbar from './navbar/Navbar'; // Import Navbar component
import BannerSlider from './Banners/BannerSlider';
import Homecategories from './category/HomeCategory';
import ProductList from './products/ProductList';
import Footer from './Footer/Footer';
import { AuthProvider } from '../provider/AuthProvider';
import Routing from '../routes/Routing';

const Buyer = () => {
  const [selectedCategory, setSelectedCategory] = useState('Vegetables');
  const [cartCount, setCartCount] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchCartCount();
  }, []);

  const fetchCartCount = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found');
      }
      const response = await axios.get('http://localhost:5000/api/cart/count', {
        headers: { Authorization:` Bearer ${token} `}
      });
      if (response.data.success) {
        setCartCount(response.data.cartCount);
      }
    } catch (error) {
      console.error('Error fetching cart count:', error);
    }
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  return (
    <div className="buyer-container">
      <Navbar /> {/* Include the Navbar component */}
      <SearchBar onSearch={handleSearch} />
      {!searchQuery && (
        <div className="slider-container">
          <BannerSlider />
        </div>
      )}
      {!searchQuery && <Homecategories onCategorySelected={handleCategorySelect} />}
      <AuthProvider>
        <Routing />
      </AuthProvider>
      <Footer />
    </div>
  );
};

export default Buyer;
